sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	if (newValue == 'Интернет-сайт'){
		g_form.setValue('u_bq_satisfaction', '0');
		g_form.setValue('u_bq_no_satisfaction', 'true');
		if (g_form.getValue('u_bq_danger.u_autoclose') == 'true' && g_form.getValue('u_bq_danger.u_feedback') == 'false')
			g_form.setValue('u_bq_resptext','<p>Ваше предложение отправлено на рассмотрение ответственному лицу. В случае заинтересованности с вами обязательно свяжутся.</p>');
	}
   //Type appropriate comment here, and begin script below

}
